<?phpclass Kiinteisto extends BaseModel{		public $id, $nimi, $katuosoite, $postinumero, $postitoimipaikka;	public function __construct($attributes){		parent::__construct($attributes); 		}	//OK	public function save(){		$query = DB::connection()->prepare('INSERT INTO Kiinteisto (nimi, katuosoite, postinumero, postitoimipaikka) VALUES (:nimi, :katuosoite, :postinumero, :postitoimipaikka) RETURNING id');		$query->execute(array('nimi' => $this->nimi, 'katuosoite' => $this->katuosoite, 'postinumero' => $this->postinumero, 'postitoimipaikka' => $this->postitoimipaikka));		$row = $query->fetch();		//Kint::trace();  		//Kint::dump($row);		$this->id = $row['id'];	}	public static function all(){		$query = DB::connection()->prepare('SELECT * FROM Kiinteisto');		$query->execute();		$rows = $query->fetchAll();		$kiinteistot = array();		foreach ($rows as $row) {			$kiinteistot[] = new Kiinteisto(array(				'id' => $row['id'],				'nimi' => $row['nimi'],				'katuosoite' => $row['katuosoite'],				'postinumero' => $row['postinumero'],				'postitoimipaikka' => $row['postitoimipaikka']			));			}		return $kiinteistot;	}	public static function findById($id){		$query = DB::connection()->prepare('SELECT * FROM Kiinteisto WHERE id = :id LIMIT 1'); 		$query->execute(array('id' =>$id));		$row = $query->fetch();				if ($row) {			$haettukiinteisto[] = new Kiinteisto(array(				'id' => $row['id'],				'nimi' => $row['nimi'],				'katuosoite' => $row['katuosoite'],				'postinumero' => $row['postinumero'],				'postitoimipaikka' => $row['postitoimipaikka']			));				return $haettukiinteisto;		}		return null;  	}}